#include "Animation_pt2.h"

Animation_pt2::Animation_pt2(DMDFrame &dmd, SPIDMD &dmdbright, int width, int height) : dmd(dmd),
                                                                                        dmdbright(dmdbright),
                                                                                        width(width),
                                                                                        height(height)
{
}

// *********************************************************************************************
// effects

void Animation_pt2::drawPicture(int x, int y, bool *p, uint16_t rows, uint16_t cols, String style)
{
    width = dmd.width;
    height = dmd.height;
    if (style == "none")
    {
        for (uint16_t j = 0; j < cols; j++)
        {
            if (x + j >= 0 && x + j <= width)
            {
                for (uint16_t i = 0; i < rows; i++)
                {
                    if (y + i >= 0 && y + i <= height)
                    {
                        if (*(p + i * cols + j))
                            dmd.setPixel(x + j, y + i, GRAPHICS_ON);
                        else
                            dmd.setPixel(x + j, y + i, GRAPHICS_OFF);
                    }
                }
            }
        }
    }
    else if (style == "lines_to_right")
    {
        for (uint16_t j = 0; j < cols; j++)
        {
            if (x + j >= 0 && x + j <= width)
            {
                for (uint16_t i = 0; i < rows; i++)
                {
                    if (y + i >= 0 && y + i <= height)
                    {
                        if (*(p + i * cols + j))
                            dmd.drawLine(x + j, y + i, width, y + i, GRAPHICS_ON);
                        else
                            dmd.drawLine(x + j, y + i, width, y + i, GRAPHICS_OFF);
                    }
                }
            }
            delay(20);
        }
        for (uint16_t i = 0; i < height; i++)
            dmd.drawLine(x + cols, y + i, width, y + i, GRAPHICS_OFF);
    }
    else if (style == "stack")
    {
        for (uint16_t i = rows; i > 0; i--)
        {
            if (y + i - 1 >= 0 && y + i - 1 <= height)
            {
                for (uint16_t k = 0; k <= i - 1; k++)
                {
                    for (uint16_t j = 0; j < cols; j++)
                    {
                        if (x + j >= 0 && x + j <= width)
                        {
                            if (*(p + (i - 1) * cols + j))
                            {
                                dmd.setPixel(x + j, y + k, GRAPHICS_ON);
                                dmd.setPixel(x + j, y + k - 1, GRAPHICS_OFF);
                            }
                        }
                    }
                    delay(20);
                }
            }
        }
    }
    else if (style == "fall")
    {
        uint16_t j_temp;
        for (uint16_t i = rows; i > 0; i--)
        {
            if (y + i - 1 >= 0 && y + i - 1 <= height)
            {
                for (uint16_t j = 0; j < cols; j++)
                {
                    if (x + j >= 0 && x + j <= width)
                    {
                        if (i % 2 == 0)
                            j_temp = j;
                        else
                            j_temp = cols - j;
                        for (uint16_t k = 0; k <= i - 1; k++)
                        {
                            if (*(p + (i - 1) * cols + j_temp))
                            {
                                dmd.setPixel(x + j_temp, y + k, GRAPHICS_ON);
                                dmd.setPixel(x + j_temp, y + k - 1, GRAPHICS_OFF);
                                delay(5);
                            }
                        }
                    }
                }
            }
        }
    }
}

// *********************************************************************************************
// effects

void Animation_pt2::fadingEffect()
{

    for (int j = 0; j < 2; j++)
    {
        for (int i = 255; i > 50; i--)
        {
            dmdbright.setBrightness(i);
            delay(20);
        }
        for (int i = 50; i < 255; i++)
        {
            dmdbright.setBrightness(i);
            delay(20);
        }
    }

    dmdbright.setBrightness(255);
}

void Animation_pt2::cleaningCurtain()
{
    width = dmd.width;
    height = dmd.height;
    for (int j = 0; j <= width / 2; j++)
    {
        for (int i = 0; i < height; i++)
        {
            dmd.setPixel(j, i, GRAPHICS_OFF);
            dmd.setPixel(width - j, i, GRAPHICS_OFF);
        }
        delay(10);
    }
}

